// funcoes desse tipo, geralmente ficariam armazendas nos recursos do roteador. Deixei dessa forma pra ficar mais simples a entrega.
function CreateCarousel() {
  const itemType = "application/vnd.lime.document-select+json";
  const items = [];

  const addCard = (cardTitle, cardSubTitle, imageUrl) => {
    const header = new Header(
      MIME_TYPES.MEDIA_LINK,
      new HeaderValue(cardTitle, cardSubTitle, IMAGE_TYPES.PNG, imageUrl)
    );

    const options = [];
    const withOptions = (optionText) => {
      return options.push({
        label: new Option(MIME_TYPES.TEXT, optionText),
      });
    };

    const item = { header, options };
    items.push(item);

    return { withOptions };
  };

  return { itemType, items, addCard };
}

const MIME_TYPES = {
  MEDIA_LINK: "application/vnd.lime.media-link+json",
  TEXT: "text/plain",
};

const IMAGE_TYPES = {
  PNG: "image/png",
};

function Header(mimeType, HeaderValue) {
  this.type = mimeType;
  this.value = HeaderValue;
}

function HeaderValue(cardTitle, cardSubTitle, imageType, imageUrl) {
  this.title = cardTitle;
  this.text = cardSubTitle;
  this.type = imageType;
  this.uri = imageUrl;
}

function Option(mimeType, OptionValue) {
  this.type = mimeType;
