}

function run(userInputContent, orderedCarouselContentsView) {
  orderedCarouselContentsView = JSON.parse(orderedCarouselContentsView);

  // added generic option in case user choose ambiguite 'saber mais'
  orderedCarouselContentsView.push({
    name: "Ambiguo",
    description: "UsuÃ¡rio digitou apenas 'saber mais' sem especificar",
    buttonText: "Saber mais",
  });

  const options = orderedCarouselContentsView.map((option) => {
    return new Option(option.name, option.description, option.buttonText);
  });

  const userChoose = Processchoose(userInputContent, options);

  if (userChoose.name === "AMBIGUO") {
    return {
      ...userChoose,
      isValid: false,
    };
  }

  return userChoose;
}

function Processchoose(userInput, arrOptions) {
  const encodedUserInput = encodeURI(userInput.toUpperCase());
  const encodedNormalizedOptions = arrOptions.map((option) => {
    return {
      ...option,
      name: option.name.toUpperCase(),
      description: option.description.toUpperCase(),
      choose: encodeURI(option.choose.toUpperCase()),
    };
  });

  const choose = encodedNormalizedOptions.filter(
    (option) => option.choose === encodedUserInput
  );

  if (choose.length == 0) {
    return new Option(
      "Input inesperado",
      "UsuÃ¡rio enviou um input nÃ£o mapeado",
      null,
      false
    );
  }

  return choose[0];
}

function Option(name, description, choose, isValid = true) {
  this.name = name;
  this.description = description;
  this.choose = choose;
  this.isValid = isValid;
}

